import { Component } from '@angular/core';

@Component({
  selector: 'app-pipe-customizado',
  templateUrl: './pipe-customizado.component.html',
  styleUrls: ['./pipe-customizado.component.css']
})
export class PipeCustomizadoComponent {

}
<h1>Pipe customizado</h1>

<h2>Nesse exemplo, customizados um pipe com a função de multiplicar 
    dois valores. Lembre-se que para utilizar o pipe é necessário que
    esteja dentro das 4 chaves. ((</h2>

<p>{{ 2 | multiplicaPor: 2 }}</p>
<p>{{ 3 | multiplicaPor: 2 }}</p>
<p>{{ 5 | multiplicaPor: 2 }}</p>
<p>{{ 5 | multiplicaPor: 5 }}</p>
<p>{{ 3 | multiplicaPor: 7 }}</p>
